CREATE TABLE `webstories`.`users` (
  `userId` INT NOT NULL AUTO_INCREMENT,
  `userName` VARCHAR(45) NOT NULL,
  `password` VARCHAR(45) NOT NULL,
  `email` VARCHAR(45) NOT NULL,
  PRIMARY KEY (`userId`));

CREATE TABLE `webstories`.`story` (
`storyId` INT NOT NULL AUTO_INCREMENT,
`userId` INT NOT NULL,
`category` VARCHAR(45) NOT NULL,
`title` VARCHAR(45) NOT NULL,
`summary` Text ,
`storyContent` TEXT NOT NULL,
`storyViews` INT DEFAULT 0,
`downloads` INT DEFAULT 0,
  PRIMARY KEY (`storyId`));

CREATE TABLE `webstories`.`story_views`(
`storyId` INT NOT NULL,
`userViewed` INT DEFAULT 0, 
`viewTimeStamp` DATE DEFAULT 0
);

CREATE TABLE `webstories`.`story_downloads`(
`storyId` INT NOT NULL,
`user` INT DEFAULT 0, 
`download_TimeStamp` DATE DEFAULT 0
);

